// components/AuthorList.js
import React, { useEffect, useState } from 'react';
import AuthorService from '../services/AuthorService';
import { Link } from 'react-router-dom';

function AuthorList() {
    const [authors, setAuthors] = useState([]);

    useEffect(() => {
        // Fetch authors here
        // Assuming an endpoint exists to get all authors
        AuthorService.getAllAuthors().then((response) => {
            setAuthors(response.data);
        });
    }, []);

    return (
        <div className="container">
            <h2>Authors</h2>
            <Link to="/authors/new" className="btn btn-primary mb-2">
                Add Author
            </Link>
            <table className="table table-bordered">
                <thead>
                <tr>
                    <th>Name</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {authors.map((author) => (
                    <tr key={author.id}>
                        <td>{author.name}</td>
                        <td>
                            <Link to={`/authors/${author.id}`} className="btn btn-info">
                                View
                            </Link>
                            <Link to={`/authors/edit/${author.id}`} className="btn btn-warning ml-2">
                                Edit
                            </Link>
                            <button
                                className="btn btn-danger ml-2"
                                onClick={() => deleteAuthor(author.id)}
                            >
                                Delete
                            </button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );

    function deleteAuthor(id) {
        AuthorService.deleteAuthor(id).then(() => {
            setAuthors(authors.filter((author) => author.id !== id));
        });
    }
}

export default AuthorList;
